INSERT INTO `question` VALUES ('164', '1', '遇有浓雾或特大雾天能见度过低，行车困难时，应怎样做？', null, '2', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('165', '1', '车辆涉水后，应保持低速行驶，怎样操作制动踏板，以恢复制动效果？', null, '2', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('166', '1', '风、雨、雪、雾等复杂气象条件，遇前车速度较低时，应开启前照灯，连续鸣喇叭迅速超越。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('167', '1', '雨天路面湿滑，车辆制动距离增大，行车中尽量使用紧急制动减速。', '因为路面比较滑，如果紧急制动（急刹车），容易造成车辆失控，所以用发动机制动。', '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('168', '1', '浓雾天气能见度低，开启远光灯会提高能见度。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('169', '1', '连续降雨天气，山区公路可能会出现路肩疏松和堤坡坍塌现象，行车时应选择道路中间坚实的路面，避免靠近路边行驶。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('170', '1', '雾天行车多使用喇叭可引起对方注意；听到对方车辆鸣喇叭，也应鸣喇叭回应。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('171', '1', '在冰雪路面上行车，必须降低车速、加大安全距离。', '冰雪路面行驶时，制动距离比平时要大。因此要考虑到紧急状况。如果出现紧急情况，需要有足够的制动距离。', '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('172', '1', '雪天行车中，在有车辙的路段应循车辙行驶。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('173', '1', '车辆在冰雪路面紧急制动易产生侧滑，应低速行驶，可利用发动机制动进行减速。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('174', '1', '车辆行至泥泞或翻浆路段时，应停车观察，选择平整、坚实的路段缓慢通过。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('175', '1', '在大雨天行车，为避免发生“水滑”而造成危险，要控制速度行驶。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:28', '2017-05-09 21:09:28');
INSERT INTO `question` VALUES ('176', '1', '前轮胎爆裂已出现转向时，驾驶人不要过度矫正，应在控制住方向的情况下，应怎样做，使车辆缓慢减速？', null, '2', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('177', '1', '轮胎气压过低时，高速行驶轮胎会出现波浪变形温度升高而导致什么？', null, '2', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('178', '1', '避免爆胎的错误的做法是什么？', null, '2', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('179', '1', '驾驶人发现轮胎漏气，将车辆驶离主车道时，不要采用紧急制动，以免造成翻车或后车采取制动不及时导致追尾事故。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('180', '1', '车辆后轮胎爆裂，车尾会摇摆不定，驾驶人应双手紧握转向盘，控制车辆保持直线行驶，减速停车。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('181', '1', '行车中当驾驶人意识到爆胎时，应在控制住方向的情况下，轻踏制动踏板，使车辆缓慢减速，逐渐平稳地停靠于路边。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('182', '1', '行车中当车辆突然爆胎时，驾驶人切忌慌乱中急踏制动踏板，尽量采用“抢挡”的方法，利用发动机制动使车辆减速。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('183', '1', '车辆前轮胎爆裂，危险较大，方向会立刻向爆胎车轮一侧跑偏，直接影响驾驶人对转向盘的控制。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('184', '1', '车辆发生爆胎后，驾驶人在尚未控制住车速前，不要冒险使用行车制动器停车，以避免车辆横甩发生更大的险情。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('185', '1', '行车中当车辆前轮爆胎已发生转向时，驾驶人应双手紧握转向盘，尽力控制车辆直线行驶。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('186', '1', '行车中当驾驶人意识到车辆爆胎时，应在控制住方向的情况下采取紧急制动，迫使车辆迅速停住。', null, '1', '1', '3', null, '1', '2017-05-09 21:09:29', '2017-05-09 21:09:29');
INSERT INTO `question` VALUES ('187', '2', '驾驶机动车在路口直行遇到这种信号灯应该怎样行驶？', '红灯请停在停止线内。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85rgmjgtlj20b404e74w.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('188', '2', '驾驶机动车行驶到这个位置时，如果车前轮已越过停止线可以继续通过。', '都红灯了，赶紧停车吧。', '1', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85riphah9j20dw05i754.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('189', '2', '驾驶机动车在这种信号灯亮的路口，可以右转弯。', '没有问题，只要安全的情况下就可以右转，前边的红灯是管直行的。', '1', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85rkuaghgj20dw05pq3p.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('190', '2', '驾驶机动车在路口遇到这种信号灯禁止通行。', '绿灯行。不是禁止通行。', '1', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85rmxt9noj20dw05n75c.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('191', '2', '绿灯亮表示前方路口允许机动车通行。', '常识题', '1', '1', '3', null, '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('192', '2', '驾驶机动车在路口遇到这种信号灯亮时，要在停止线前停车瞭望。', '绿灯，赶紧的走你。', '1', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85rp0ufrxj20dw05nq3m.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('193', '2', '驾驶机动车在路口遇到这种信号灯亮时，不能右转弯。', '右转灯不是红灯，右转没问题。', '1', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85rr4fecwj20dw05it9l.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('194', '2', '驾驶机动车遇到这种信号灯，可在对面直行车前直接向左转弯。', '左转让直行。', '1', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85rt8bnuuj20dw05haay.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('195', '2', '驾驶机动车在路口遇到这种信号灯表示什么意思？', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85rvd9p3oj20dw05odgu.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('196', '2', '驾驶机动车在路口看到这种信号灯亮时，要加速通过。', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', '1', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85rxg4kjij20dw05iwfe.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('197', '2', '驾驶机动车在前方路口不能右转弯。', '黄灯闪烁是警示的意思，没有不能右转的意思。', '1', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85rzkuy06j20dw05it9l.jpg', '1', '2017-05-09 21:09:32', '2017-05-09 21:09:32');
INSERT INTO `question` VALUES ('198', '2', '驾驶机动车遇到这种信号灯亮时，如果已越过停止线，可以继续通行。', '规定：当黄灯亮起的时候，车身已越过停止线，可以继续通行。', '1', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85s1pso3wj20dw05pjs5.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('199', '2', '遇到这种情况时怎样行驶？', '俩侧车道都是红灯都不可以通行所以是禁止车辆在两侧车道通行。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85s3uystlj20dw05ljsb.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('200', '2', '遇到这种情况时，中间车道不允许车辆通行。', '中间车道是红灯故不允许车辆通行。', '1', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85s6036yej20dw05lq3w.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('201', '2', '这辆红色轿车可以在该车道行驶。', '看红色轿车行驶的车道的信号灯是红色，所以是不能在该道行驶的。', '1', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85s83s6mxj20dw05lmy0.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('202', '2', '驾驶机动车要选择绿色箭头灯亮的车道行驶。', '绿灯行，没有问题。', '1', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85sa6z8uwj20dw05haaz.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('203', '2', '这个路口允许车辆怎样行驶？', '请看灯，直行和右转灯是绿的。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85scanpdfj20dw05ujs6.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('204', '2', '这个路口允许车辆怎样行驶？', '左转的灯是绿色，所以可以向左转弯。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85sefqm7ej20dw05oq3x.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('205', '2', '这个路口允许车辆怎样行驶？', '右转的灯是绿的，所以只能向右转弯。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85sgk39clj20dw05ut9i.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('206', '2', '驾驶机动车在这种情况下不能左转弯。', '左转红灯，所以不能左转，正确。', '1', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85sioobvuj20dw05mgml.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('207', '2', '驾驶机动车在这种情况下可以右转弯。', '因为右转的灯是红色的所以是不能右转的。<br&nbsp;/>扩展阅读：右转信号灯只可能是箭头形状不可能是圆形的。', '1', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85sktee9aj20dw05mgml.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('208', '2', '驾驶机动车在这种情况下不能直行和左转弯', '直行和左转都是红灯，所以不能直行和左转。', '1', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85smwam1yj20dw05mdgt.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('209', '2', '驾驶机动车遇到这种信号灯不断闪烁时怎样行驶？', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85sp0ecpnj20dw05mjsf.jpg', '1', '2017-05-09 21:09:33', '2017-05-09 21:09:33');
INSERT INTO `question` VALUES ('210', '2', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', '路口黄灯持续闪烁，警示驾驶人要注意瞭望，确认安全通过。', '1', '1', '3', null, '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('211', '2', '黄灯持续闪烁，表示机动车可以加速通过。', '黄灯持续闪烁的意思是，提示用户谨慎通行，此刻应该放慢速度谨慎通过。', '1', '1', '3', null, '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('212', '2', '驾驶机动车在铁路道口看到这种信号灯时怎样行驶？', '红灯，你就停了吧。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85sr52te9j20dw05ljsc.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('213', '2', '在铁路道口遇到两个红灯交替闪烁时要停车等待。', '两个红灯交替闪烁时表示火车要经过了，赶紧停车等待吧。', '1', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85st8jkoxj20dw05lmy4.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('214', '2', '在道路与铁路道口遇到一个红灯亮时要尽快通过道口。', null, '1', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85svbyx14j20dw05ljsc.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('215', '2', '这属于哪一种标志？', '交叉路口：用以警告车辆驾驶人谨慎慢行，注意横向来车。设在平面交叉路口驶入路段的适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85sxgj0nfj205v05kq30.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('216', '2', '这个标志是何含义？', '十字交叉路口：除了基本形十字路口外，还有部分变异的十字路口，如：五路交叉路口、变形十字路口、变形五路交叉路口等。五路以上的路口均按十字路口对待。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85szjm74lj205v05kglo.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('217', '2', '这个标志是何含义？', 'T型交叉路口：丁字形标志原则上设在与交叉口形状相符的道路上。右侧丁字路口，此标志设在进入T字路口以前的适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85t1o9eigj206705kjrg.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('218', '2', '这个标志是何含义？', '向右急转弯：向右急弯路标志，设在右急转弯的道路前方适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85t3ru97hj206705kt8t.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('219', '2', '这个标志是何含义？', '向左急转弯：向左急弯路标志设在左急转弯的道路前方适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85t5wo5dpj206705kt8t.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('220', '2', '这是什么交通标志？', '反向弯路：用以警告车辆驾驶人减速慢行。设置位置为两反向圆曲线起点的外面，但不应进入相邻的圆曲线内。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85t8034udj206705kjrh.jpg', '1', '2017-05-09 21:09:34', '2017-05-09 21:09:34');
INSERT INTO `question` VALUES ('221', '2', '这是什么交通标志？', '连续弯路：用以警告车辆驾驶人减速慢行。设置位置为连续弯路起点的外面，当连续弯路总长度大于500m时，应重复设置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85ta4hpz8j206305kaa5.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('222', '2', '这个标志是何含义？', '上陡坡：用以提醒车辆驾驶人小心驾驶。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85tc90vtqj206705kwel.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('223', '2', '这个标志是何含义？', '下陡坡：用以提醒车辆驾驶人小心驾驶。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85teefsu8j206705kt8t.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('224', '2', '这个标志是何含义？', '连续下坡：用以提醒车辆驾驶人小心驾驶。当连续下坡总长大于3&nbsp;km后，应重复设置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e85tgidof8j206305kt8t.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('225', '2', '这是什么交通标志？', '两侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6&nbsp;m以下的路段起点前方。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85timzb5wj206705kjrh.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('226', '2', '这是什么交通标志？', '右侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6&nbsp;m以下的路段起点前方。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85tks8wapj206705k74d.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('227', '2', '这是什么交通标志？', '左侧变窄：用以警告车辆驾驶人注意前方车行道或路面狭窄情况，遇有来车应予减速避让。设在双车道路面宽度缩减为6&nbsp;m以下的路段起点前方。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85tmvh0uzj206705kglp.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('228', '2', '这个标志是何含义？', '窄桥：用以警告车辆驾驶人注意前方桥面宽度变窄，应谨慎驾驶。设在桥面净宽较两端路面宽度变窄，且桥的净宽小于6&nbsp;m的桥梁以前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85tp0808mj206305kq31.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('229', '2', '这个标志是何含义？', '双向交通：用以提醒车辆驾驶人注意会车。设在由双向分离行驶，因某种原因出现临时性或永久性的不分离双向行驶的路段，或由单向行驶进人双向行驶的路段以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85tr3vigyj206c05kaa6.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('230', '2', '这个标志是何含义？', '注意行人：用以警告车辆驾驶人减速慢行，注意行人。设在行人密集，或不易被驾驶员发现的人行横道线以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85tt7p13uj206c05kglt.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('231', '2', '这个标志是何含义？', '注意儿童：用以警告车辆驾驶人减速慢行，注意儿童。设在小学、幼儿园、少年宫等儿童经常出入地点前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85tvc9j1nj206a05kmxb.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('232', '2', '这个标志是何含义？', '这个是头牛，是牲畜。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e85txh5weij206a05k3ym.jpg', '1', '2017-05-09 21:09:35', '2017-05-09 21:09:35');
INSERT INTO `question` VALUES ('233', '2', '这个标志是何含义？', '这个是头鹿啊，是野生动物啊。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e85tzm1jkxj206a05kglq.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('234', '2', '这个标志是何含义？', '注意信号灯：用以警告车辆驾驶人注意前方路段设有信号灯，应依信号灯指示行车。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85u1qjvlnj206a05k3ym.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('235', '2', '这个标志是何含义？', '注意落石：此标志设在左侧有落石危险的傍山路段之前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86q4rh01zj206705kmxa.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('236', '2', '这个标志是何含义？', '注意横风：此标志设在经常有很强的侧风并有必要引起注意的路段前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86q6ucofij206a05k74e.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('237', '2', '这个标志是何含义？', '易滑路段：此标志设在路面的摩擦系数不能满足相应行驶速度下要求紧急刹车距离的路段前适当位置。行驶至此路段必须减速慢行。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86q8xgoqoj206a05kwen.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('238', '2', '这个标志是何含义？', '傍山险路：用以提醒车辆驾驶人小心驾驶。设在傍山险路路段以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86qb1qfyuj206705kdfx.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('239', '2', '这个标志是何含义？', '堤坝路：用以提醒车辆驾驶人小心驾驶，设在沿水库、湖泊、河流等堤坝道路以前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86qd4v9rcj206705kt8v.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('240', '2', '这个标志是何含义？', '村庄：用以提醒车辆驾驶人小心驾驶。设在紧靠村庄、集镇且视线不良的路段以前适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86qf7yjbhj206a05kwem.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('241', '2', '这个标志是何含义？', '隧道：用以提醒车辆驾驶人注意慢行。设在双向行驶并且照明不好的隧道口前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86qhciqbbj206a05kdfz.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('242', '2', '这个标志是何含义？', '渡口：用以提醒车辆驾驶人谨慎驾驶。设在车辆渡口以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86qjflbh6j206a05kt8v.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('243', '2', '这个标志是何含义？', '驼峰桥：用以提醒车辆驾驶人谨慎驾驶。设在拱度很大，影响视距的驼峰桥以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86qlio8yqj206a05kjrh.jpg', '1', '2017-05-09 21:09:36', '2017-05-09 21:09:36');
INSERT INTO `question` VALUES ('244', '2', '这个标志是何含义？', '路面不平：用以提醒车辆驾驶人减速慢行。设在路面颠簸路段或桥头跳车较严重的地点以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86qnnmb94j206b05k0st.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('245', '2', '这个标志是何含义？', '路面高突：用以提醒车辆驾驶人减速慢行。设在路面突然高突以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86qpr9ur3j206a05kt8s.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('246', '2', '这个标志是何含义？', '路面低洼：用以提醒车辆驾驶人减速慢行。设在路面突然低洼以前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86qrw2htbj206a05kt8s.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('247', '2', '这个标志是何含义？', '过水路面指的是通过平时无水或流水很少的宽浅河流而修筑的在洪水期间容许水流浸过的路面。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86qu0cg5uj206a05kdfz.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('248', '2', '这个标志是何含义？', '有火车形状的，为无人看守的铁路口，此图为有人看守的铁路口。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86qw4zcgjj206a05kq33.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('249', '2', '这个标志是何含义？', '有火车形状的，为无人看守的铁路口，还有一个栅栏形式的图片为有人看守的铁路口。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86qy9ad5uj206a05kglr.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('250', '2', '这个标志是何含义？', '多股铁路与道路相交，则应在铁路道口标志上方设置叉形符号。叉形符号交叉点到警告标志三角形顶点的距离为40cm。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86r0dtnyoj209105kjrw.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('251', '2', '这个标志是何含义？', '一道红线是50米，一道50米。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86r2ia4cyj205b05kaa1.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('252', '2', '这个标志是何含义？', '一道红线是50米，二道100米。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86r4mugp3j205b05k3yi.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('253', '2', '这个标志是何含义？', '一道红线是50米，三道150米。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86r6r3cbxj205b05kjre.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('254', '2', '这个标志是何含义？', '黄底标志为警告标志，自行车优势非机动车所以答案是注意非机动车。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86r8vt70rj206305k3yp.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('255', '2', '这个标志是何含义？', '黄底标志为警告标志，所以是注意残疾人。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86razad7xj206305kq32.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('256', '2', '这个标志是何含义？', '事故易发路段：此标志设在交通事故易发路段以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86rd3xhpnj206305kwen.jpg', '1', '2017-05-09 21:09:37', '2017-05-09 21:09:37');
INSERT INTO `question` VALUES ('257', '2', '这个标志是何含义？', '慢行：此标志设在前方需要减速慢行的路段以前适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86rf8zhpbj206305kt8u.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('258', '2', '这个标志是何含义？', '左右绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86rhdi633j206305k74e.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('259', '2', '这个标志是何含义？', '左侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86rjg4sobj206305k0st.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('260', '2', '这个标志是何含义？', '右侧绕行：用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86rlj9zprj206305k74d.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('261', '2', '这个标志是何含义？', '注意危险：用以促使车辆驾驶员谨慎慢行。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86rnm78sfj206305kjrg.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('262', '2', '这个标志是何含义？', '施工路段：用以告示前方道路施工，车辆应减速慢行或绕道行驶。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86rpoyfuuj206i05kgls.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('263', '2', '这个标志是何含义？', '常识题，记住。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86rrtgv2ij208v03zaaa.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('264', '2', '这个标志是何含义？', '这个应该能看懂吧？很明显那个小车开灯的效果。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86rtwlwkjj206k05k3yn.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('265', '2', '这个标志是何含义？', '“潮汐车道”是指根据早晚交通流量不同情况，对有条件的道路，试点开辟潮汐车道，通过车道灯的指示方向变化，控制主干道车道行驶方向，来调整车道数。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86rvzq864j206305kwem.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('266', '2', '这个标志是何含义？', '车距确认线有三种：', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86ry49fcij206305kt8w.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('267', '2', '这个标志是何含义？', '注意分离式道路：用以警告车辆驾驶人注意前方平面交叉的被交道路是分离式道路。<br&nbsp;/>', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86s077a9nj205x05kglo.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('268', '2', '这个标志是何含义？', '黄底为警告标志，所以排除A和B，图很明显的为合流标志，所以选D', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86s2bunl0j206605kglp.jpg', '1', '2017-05-09 21:09:38', '2017-05-09 21:09:38');
INSERT INTO `question` VALUES ('269', '2', '这个标志是何含义？', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86s4g73qjj208105kaa9.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('270', '2', '禁令标志的作用是什么？', '禁令标志是交通标志中主要标志的一种，对车辆加以禁止或限制的标志，如禁止通行、禁止停车、禁止左转弯、禁止鸣喇叭、限制速度、限制重量等。', '2', '1', '3', null, '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('271', '2', '这个标志是何含义？', '停车让行：表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86s8pbtfhj205m05kwem.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('272', '2', '这个标志是何含义？', '减速让行：表示车辆应减速让行，告示车辆驾驶员必须慢行或停车，观察干路行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。此标志设在视线良好交叉道路的次要路口。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86satuaj6j206b05k3ym.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('273', '2', '这个标志是何含义？', '会车时停车让对方车先行：表示车辆会车时，必须停车让对方车先行。设置在会车有困难的狭窄路段的一端或由于某种原因只能开放一条车道作双向通行路段的一端。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86scxfw3dj205k05kaa7.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('274', '2', '这个标志是何含义？', '禁止通行：表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86sf0mgofj205k05kq30.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('275', '2', '这个标志提示哪种车型禁止通行？', '一看图片就是小型客车。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86sh45rwkj205k05kmxc.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('276', '2', '这个标志是何含义？', '禁止向左转弯：表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86sj945c6j205k05kjrj.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('277', '2', '这个标志是何含义？', '禁止向右转弯：表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86sldewd3j205k05kjrk.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('278', '2', '这个标志是何含义？', '禁止直行：表示前方路口禁止一切车辆直行。此标志设在禁止直行的路口前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86sngf6hgj205k05k0sw.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('279', '2', '这个标志是何含义？', '禁止向左向右转弯：表示前方路口禁止一切车辆向左向右转弯。此标志设在禁止向左向右转弯的路口前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86spjdszaj205k05kdg0.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('280', '2', '这个标志是何含义？', '禁止直行和向左转弯：表示前方路口禁止一切车辆直行和向左转弯。此标志设在禁止直行和向左转弯的路口前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86srnpyhdj205k05kgls.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('281', '2', '这个标志是何含义？', '禁止直行和向右转弯：表示前方路口禁止一切车辆直行和向右转弯。此标志设在禁止直行和向右转弯的路口前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86sts7z8ej205j05kgls.jpg', '1', '2017-05-09 21:09:39', '2017-05-09 21:09:39');
INSERT INTO `question` VALUES ('282', '2', '这个标志是何含义？', '禁止掉头：表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86svx17bvj205k05kq34.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('283', '2', '这个标志是何含义？', '禁止超车：表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86sy1dywdj205k05kaa9.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('284', '2', '这个标志是何含义？', '解除禁止超车：表示禁止超车路段结束。此标志设在禁止超车的终点。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86t0ndd1nj205q05kq31.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('285', '2', '这个标志是何含义？', '禁止停放车辆：表示在限定的范围内，禁止一切车辆临时或长时停放。此标志设在禁止车辆停放的地方。禁止车辆停放的时间、车种和范围可用辅助标志说明。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86t39uvkaj205k05kglr.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('286', '2', '这个标志是何含义？', '禁止长时停车：禁止车辆长时停放，临时停放不受限制。禁止车辆停放的时间、车种和范围可用辅助标志说明。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86t5wbpk3j205k05kwel.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('287', '2', '这个标志是何含义？', '禁止鸣喇叭：表示禁止鸣喇叭。此标志设在需要禁止鸣喇叭的地方。禁止鸣喇叭的时间和范围可用辅助标志说明。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86t8j5nxbj205k05kt8w.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('288', '2', '这个标志是何含义？', '限制宽度为3米：表示禁止装载宽度超过标志所示数值的车辆通行。此标志设在最大允许宽度受限制的地方。&nbsp;以图为例：装载宽度不得超过3米', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86tb4s8jvj205k05kdfz.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('289', '2', '这个标志是何含义？', '限制高度为3.5米：表示禁止装载高度超过标志所示数值的车辆通行。此标志设在最大允许高度受限制的地方。&nbsp;以图为例：装载高度不得超过3.5米。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86tdr1tv3j205k05kdg0.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('290', '2', '这个标志是何含义？', '限制最高时速40公里：表示该标志至前方限制速度标志的路段内，机动车行驶速度不得超过标志所示数值。此标志设在需要限制车辆速度的路段的起点。以图为例：限制行驶时速不得超过40公里。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86tgd6f4pj205k05kwen.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('291', '2', '这个标志是何含义？', '解除时速40公里限制：表示限制速度路段结束。此标志设在限制车辆速度路段的重点。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86tizk2n4j205k05kt8t.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('292', '2', '这个标志是何含义？', '停车检查：表示机动车必须停车接受检查。此标志设在关卡将近处，以便要求车辆接受检查或缴费等手续。标志中可加注说明检查事项。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86tllb6exj205k05k74f.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('293', '2', '这个标志是何含义？', '海关那俩个大字那么清晰你再不选A，A得有多伤心。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86to7w6sdj205k05kweo.jpg', '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('294', '2', '指示标志的作用是什么？', '指示标志是交通标志中主要标志的一种，用以指示车辆和行人按规定方向、地点行驶。&nbsp;指示标志的颜色为蓝底、白图案；形状分为圆形、长方形和正方形。', '2', '1', '3', null, '1', '2017-05-09 21:09:40', '2017-05-09 21:09:40');
INSERT INTO `question` VALUES ('295', '2', '这个标志是何含义？', '只准直行：表示只准一切车辆直行。此标志设在直行的路口以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86ttgyq3uj205k05kwei.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('296', '2', '这个标志是何含义？', '向左转弯：表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86tw4321lj205k05kq2y.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('297', '2', '这个标志是何含义？', '向右转弯：表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86tyqkx7dj205k05kt8q.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('298', '2', '这个标志是何含义？', '直行和向右转弯：表示只准一切车辆直行和向右转弯。此标志设在车辆必须直行和向右转弯）的路口以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86u1cmfklj205k05kq2z.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('299', '2', '这个标志是何含义？', '直行和向左转弯：表示只准一切车辆直行和向左转弯。此标志设在车辆必须直行和向左转弯的路口以前适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86u3z25t5j205k05kq2z.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('300', '2', '这个标志是何含义？', '向左和向右转弯：表示只准一切车辆向左和向右转弯。此标志设在车辆必须向左和向右转弯的路口以前适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86u6ljbxqj205k05k74c.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('301', '2', '这个标志是何含义？', '靠右侧道路行驶：表示只准一切车辆靠右侧道路行驶。此标志设在车辆必须靠右侧行驶的路口以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86u97ndtsj205k05k3yj.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('302', '2', '这个标志是何含义？', '靠左侧道路行驶：表示只准一切车辆靠左侧道路行驶。此标志设在车辆必须靠左侧行驶的路口以前适当位置。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86ubuc78wj205k05k0sr.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('303', '2', '这个标志是何含义？', '立体交叉直行和左转弯行驶：表示车辆在立交处可以直行和按图示路线左转弯行驶。此标志设在立交左转弯出口处适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86ueghwq5j205k05kmx8.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('304', '2', '这个标志是何含义？', '立体交叉直行和右转弯行驶：表示车辆在立交处可以直行和按图示路线右转弯行驶。此标志设在立交右转弯出口处适当位置。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86uh2ec94j205k05kq30.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('305', '2', '这个标志是何含义？', '环岛行驶：表示只准车辆靠右环行。此标志设在环岛面向路口来车方向适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86ujonulnj205k05kmx9.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('306', '2', '这个标志是何含义？', '表示一切车辆向左单向行驶。此标志设在单行路的路口和入口处的适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86umbbb6cj209k05kaa6.jpg', '1', '2017-05-09 21:09:41', '2017-05-09 21:09:41');
INSERT INTO `question` VALUES ('307', '2', '这个标志是何含义？', '表示一切车辆向右单向行驶。此标志设在单行路的路口和入口处的适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86uoxhaxbj209k05kglq.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('308', '2', '这个标志是何含义？', '表示一切车辆单向行驶。此标志设在单行路的路口和入口处的适当位置。', '2', '1', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e86urjcefuj203805k0so.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('309', '2', '这个标志是何含义？', '表示该街道只供步行。此标志设在步行街的两端。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86uu65nd0j205k05k74d.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('310', '2', '这个标志是何含义？', '表示机动车行至该标志处必须鸣喇叭。此标志设在公路的急转弯处、陡坡等视线不良路段的起点。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86uwsa82dj205k05kq2z.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('311', '2', '这个标志是何含义？', '表示该处为专供行人横穿马路的通道。此标志设在人行横道的两侧。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86uzeqpmwj205h05kgln.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('312', '2', '这个标志是何含义？', '表示机动车驶入前方道路之最低时速限制。此标志设在高速公路或其他道路限速路段的起点。<br&nbsp;/>高度限速标志是白底红字。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86v20vj5nj205k05kmx8.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('313', '2', '这个标志是何含义？', '表示干路先行，此标志设在车道以前适当位置。<br&nbsp;/>答题技巧：记住哥一句话，谁粗谁先行。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e86v4mn4h3j205m05k3yh.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('314', '2', '这个标志是何含义？', '表示会车先行，此标志设在车道以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86v78f50fj205k05kglm.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('315', '2', '这个标志是何含义？', '表示只准一切车辆向右转弯。此标志设在车辆必须向右转弯的路口以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86v9ut7a0j205k05k3yl.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
INSERT INTO `question` VALUES ('316', '2', '这个标志是何含义？', '表示只准一切车辆向左转弯。此标志设在车辆必须向左转弯的路口以前适当位置。', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e86vciln2nj205k05k74d.jpg', '1', '2017-05-09 21:09:42', '2017-05-09 21:09:42');
