INSERT INTO `question` VALUES ('353', '2', '这个标志是何含义？', '交通监控设备标志属于指路标志，蓝底、白图形、白边框、蓝色衬边。用于告知机动车驾驶人该区域设置有固定式交通监控设备。', '2', '1', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e86y1f2aq8j204r05kaa1.jpg', '1', '2017-05-09 21:09:45', '2017-05-09 21:09:45');
