INSERT INTO `question` VALUES ('382', '2', '路中心黄色虚线属于哪一类标线？', '道路交通标线是同标划于路面上的各种线条、箭头、文字、立面标记、突起路标和轮廓标等所构成的交通安全设施。它的作用是管制和引导交通。可以与标志配合使用，也可单独使用。', '2', '1', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e87087kh3hj20i205jt8s.jpg', '1', '2017-05-09 21:09:48', '2017-05-09 21:09:48');
