INSERT INTO `question` VALUES ('672', '3', '这样临时停放红色轿车有什么违法行为？', '图为加油站，规定加油站50米内不得停车。<br&nbsp;/>公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点３０米以内的路段，除使用上述设施的以外，不得停车；', '2', '1', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e85qejrk6lj20f706y757.jpg', '1', '2017-05-09 21:10:16', '2017-05-09 21:10:16');
