INSERT INTO `question` VALUES ('1274', '7', '驾驶机动车进入高速公路加速车道后，尽快将车速提高到多少？', '有的学员会觉得题目有问题，认为尽快提速不对。但大家要看清楚前提：进入高速公路加速车道后，也就是说已经在高速公路上行驶了一定距离了。因此根据高速公路行驶车速不得低于60公里/时的规定，因此要尽快提速到60公里/时。所以选D。', '2', '4', '3', null, '1', '2017-05-09 21:13:11', '2017-05-09 21:13:11');
INSERT INTO `question` VALUES ('1275', '7', '驾驶机动车进入高速公路隧道前需要注意什么？', '进入隧道时相当于夜间行驶了，因此要开启近光灯。因此本题选C。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5ndo76ox1j20dw05kgm2.jpg', '1', '2017-05-09 21:13:11', '2017-05-09 21:13:11');
