INSERT INTO `question` VALUES ('1277', '7', '机动车从匝道驶入高速公路，应当开启什么灯？', '从匝道驶入高速公路，是由右向左进入，因此要开启左转向灯，因此本题选A。', '2', '4', '3', null, '1', '2017-05-09 21:13:11', '2017-05-09 21:13:11');
INSERT INTO `question` VALUES ('1278', '7', '机动车驶离高速公路时，应当开启什么灯？', '驶离高速公路，就是靠右侧驶离，因此要开启的应该是右转向灯。此情况下与危险报警闪光灯或是前照灯无关。因此本题选B。', '2', '4', '3', null, '1', '2017-05-09 21:13:11', '2017-05-09 21:13:11');
