INSERT INTO `question` VALUES ('1295', '7', '驾驶机动车进入高速公路加速车道后再开启左转向灯。', '要先开启左转向灯并确保安全之后才能向左进入高速公路加速车道，因此本题错误。', '1', '4', '3', null, '1', '2017-05-09 21:13:13', '2017-05-09 21:13:13');
