INSERT INTO `question` VALUES ('1298', '7', '机动车在高速公路匝道提速到每小时60公里以上时，可直接驶入行车道。', '错误的关键在于“直接”两个字，正确的做法是应该先打开左转向灯，再在不防碍已在高速公路内的机动车正常行驶的情况下驶入车道。', '1', '4', '3', null, '1', '2017-05-09 21:13:13', '2017-05-09 21:13:13');
