INSERT INTO `question` VALUES ('1383', '9', '驾驶人进入驾驶室前，首先要做什么？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:02', '2017-05-09 21:14:02');
INSERT INTO `question` VALUES ('1384', '9', '出车前对轮胎进行哪些方面的检查？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:02', '2017-05-09 21:14:02');
INSERT INTO `question` VALUES ('1385', '9', '出车前检查冷却液、发动机机油、燃油等是否有渗漏现象。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:02', '2017-05-09 21:14:02');
INSERT INTO `question` VALUES ('1386', '9', '行车前应对机动车驾驶室、发动机舱、车外部、轮胎进行检查。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:02', '2017-05-09 21:14:02');
INSERT INTO `question` VALUES ('1387', '9', '事故中造成这个驾驶人致命伤害的原因是什么？', '有图可看出是驾驶员在冲撞下向前扑出，因为没有安全带的保护，才会致使驾驶员向前倾，因此本题选A。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e83s3ffp77j20dw073aag.jpg', '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1388', '9', '机动车在紧急制动时ABS 系统会起到什么作用？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1389', '9', '驾驶机动车的驾驶人不需要系安全带。', '开车一定要系安全带，不系属于违法行为。', '1', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1390', '9', '安装防抱死制动装置（ABS）的机动车紧急制动时，可用力踏制动踏板。', 'ABS技术的出现，就是大大地降低了侧滑，和原地掉头的现象，保证了行驶的安全。如果，你在使用带ABS的车辆，在刹车时的动作，类似不带ABS的车这样轻轻的踩下去，很可能会不能触发ABS系统，也就起不到ABS的作用了，所以，一般就是要求，刹车动作要果断，一脚就踩到底，保证ABS的正确执行。', '1', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1391', '9', '驾驶有ABS 系统的机动车在紧急制动的同时转向可能会发生侧滑。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1392', '9', '安装防抱死制动装置（ABS）的机动车制动时，制动距离会大大缩短。', '这种错误认识是由于不了解ABS的工作原理导致的。ABS可以兼顾车辆行驶中的最大的纵向制动力和横向抓地力，有效地保证车辆不会发生失控状况，但不是所有配置了ABS的车辆都会缩短制动距离，如冰雪、潮湿路面上的制动距离比车轮抱死时短一些，但在一般路面上的制动距离反而更长。', '1', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1393', '9', '在这种气象条件下起步要注意哪些方面？', '前面都有雾了，那不开雾灯开什么灯？', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e83s8o7vkwj20dw05kt8v.jpg', '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1394', '9', '在这种环境中安全起步怎样使用灯光？', '夜间起步应开启近光灯，因此本题选C。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83sdwexvdj20dw04yt8s.jpg', '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1395', '9', '在这种能见度的情况下起步要开启近光灯。', '冰雪等天气情况下起步要开启近光灯，因此本题正确。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e83sj55lhmj20dw05k0t4.jpg', '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1396', '9', '驾驶机动车在这种环境条件下起步前要开启远光灯。', '起步开近光灯，牢记！先近后远！', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e83sodmuu1j20dw05b3yl.jpg', '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1397', '9', '驾驶机动车在雨天起步前要使用刮水器。', '刮水器是为了提高汽车在雨天和雪天行驶时驾驶员的能见度而专门设置的，也就是说使用刮水器，在雨天能够让驾驶员能够清楚地透过前挡风玻璃了解周围情况，因此本题正确。', '1', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1398', '9', '机动车在路边起步后应尽快提速，并向左迅速转向驶入正常行驶道路。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:03', '2017-05-09 21:14:03');
INSERT INTO `question` VALUES ('1399', '9', '机动车在路边起步后，应随时注意机动车两侧道路情况，向左缓慢转向，逐渐驶入正常行驶道路。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1400', '9', '在这种情况下驾驶人需要注意什么？', null, '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e83stlvfl6j20dw05kq3e.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1401', '9', '驾驶机动车在有这种标志的路口怎样通过最安全？', '如图交通标志是减速让行标志，因此在此路段要减速慢行，应当注意的还有前方情况，因此本题选D，不能选C。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e83sytzh32j20dw05k74u.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1402', '9', '驾驶机动车在有这种标志的路口怎样通过最安全？', '前方有个“停”的牌子，肯定是要停车观察在通过！', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83t42ynffj20dw05kgm5.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1403', '9', '驾驶机动车在这种情况下怎样汇入主路车流？', '前方有个“让”的牌子，所以要让行红色的车子！', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83tnkq0g5j20dw05kjru.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1404', '9', '驾驶机动车汇入车流时不能影响其他机动车通行。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1405', '9', '驾驶机动车从辅路汇入主路车流时要迅速。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1406', '9', '驾驶机动车向左变更车道遇到这种情况要注意让行。', '如图情况时，车辆若是想要向左变更车道，就应当让左侧车辆通过后再变道，因此本题正确。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e83tsudctqj20dw05kq3j.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1407', '9', '驾驶机动车遇到这种情况要迅速向左变更车道。', '由图可知，左侧后方有来车，所以为了避免发生碰撞及影响其他车辆正常通行，因此就算要变更车道也应该等后车通过后再变更。正确的做法是，提前开启转向灯，确保左侧车道后方没有车辆临近时向左变更车道。因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83ty2d0cmj20dw05k3z2.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1408', '9', '驾驶机动车在该位置不能变更车道。', '由图可知，前方是实线区，实线区不得变更车道，因此本题正确。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83u3b61g6j20dw05kaaf.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1409', '9', '驾驶机动车可在该路口处向右变更车道。', '因为已经到路口了，路口150米内不得变更车道。他停在左侧道，只能直行或左转。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e83u8rfdgwj20dw05kmxq.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1410', '9', '驾驶机动车在这种道路上怎样会车最安全？', '《中华人民共和国道路交通安全法实施条例》第四十八条&nbsp;&nbsp;在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：<br&nbsp;/><br&nbsp;/>（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离；<br&nbsp;/><br&nbsp;/>因此本题选B。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83ue6d11bj20dw05kdga.jpg', '1', '2017-05-09 21:14:04', '2017-05-09 21:14:04');
INSERT INTO `question` VALUES ('1411', '9', '在这种情况下怎样会车最安全？', '遇到前方车辆越线占道的，应当靠右侧行驶，避免发生碰撞，因此本题选C。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e83ujfl79dj20dw05kwey.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1412', '9', '驾驶机动车遇到这种情况怎样行驶最安全？', '遇到这种情况应当减速慢行，必要时停车避让，因此本题选A。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e83uonzk6hj20dw05kwey.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1413', '9', '会车中道路一侧有障碍，双方机动车应如何做？', '有障碍的你要绕开障碍的话，肯定要占被人的道，所以你要后行。', '2', '4', '3', null, '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1414', '9', '机动车在狭窄的坡路会车时，正确的会车方法是什么？', '《中华人民共和国道路交通安全法实施条例》《中华人民共和国道路交通安全法实施条例》第四十八条&nbsp;&nbsp;在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：<br&nbsp;/><br&nbsp;/>（三）在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；<br&nbsp;/><br&nbsp;/>因此本题选A。', '2', '4', '3', null, '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1415', '9', '会车遇到这种情况要低速会车或停车让行。', '由图可知，前方是窄桥，要避免在窄桥上会车，因此本题正确。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e9b8vfa45wj20dw05kgm3.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1416', '9', '驾驶机动车遇到这种情况时要向左占道行驶。', '遇到这种情况要减速慢行，必要时停车，因此本题错误。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e9b90n8gi0j20dw05k3yy.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1417', '9', '如动画所示，驾驶机动车遇到这种情况要如何处置？', '由动画可知，机动车要经过路边有停车的路段，为了避免停放车辆会突然起步，因此要留出空间，同时减速慢行。所以本题选A。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e9b95vt2wmg208w040h2n.gif', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1418', '9', '如动画所示，驾驶机动车遇到这种情况要如何处置？', '由动画可知，前方路段出现了障碍物。并且左侧车道有车辆，在这样的情况下应当停车，因此本题选D。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e9b982idveg208w040x54.gif', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1419', '9', '驾驶机动车在这种情况下怎样安全行驶？', '根据有关规定，有障碍物的一方要让无障碍物的一方先行，因此本题选C。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e9bamwykrzj20dw05kdgd.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1420', '9', '驾驶机动车在这样的路面如何安全行驶？', '由图中交通标志可知前方是路面不平的道路，因此要减速慢行，避免颠簸等，所以本题选D。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5izdtq9p6j20dw05kaak.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1421', '9', '驾驶机动车遇到这种情况时对向机动车优先通过。', '根据规定，有障碍的一方要让无障碍的一方先行，因此本题正确。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5izggvxl6j20dw05kmxo.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1422', '9', '驾驶机动车遇到这种情况不要减速。', '虽然不是有障碍物的一方，但对向有机动车的情况下还是要注意减速慢行，以免对向来车直接绕过障碍物驶来，因此本题错误。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5izj2laknj20dw05k0tc.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1423', '9', '驾驶机动车遇到这种情况怎样做最安全？', '本来在这样的道路上是可以超车的，但由于对向有来车，因此避免发生擦碰，要放弃超车。因此本题选B。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5izlq4x5pj20dw05k0t8.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1424', '9', '驾驶机动车在这种道路上怎样行驶最安全？', '在这样的交通状况下，不能超车，因此本题选C。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5izoclpskj20dw05kt97.jpg', '1', '2017-05-09 21:14:05', '2017-05-09 21:14:05');
INSERT INTO `question` VALUES ('1425', '9', '驾驶机动车超车时遇到这样的情况怎样保证安全？', '前方既有停靠的车辆，也有禁止鸣喇叭标志，因此在这样的路段，为了防止停靠车辆突然起步，要保持安全距离，减速慢行通过。因此本题选A。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5izqyrek2j20dw05kwf0.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1426', '9', '驾驶机动车遇到这种情况怎样礼让？', '只有D最和谐，快选它吧。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5iztlm09oj20dw05kmxq.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1427', '9', '如动画所示，前车遇到这种情况要如何处置？', '由动画可知，此时前车应当让后车超越，所以前车应当减速靠右侧行驶，并让后车超越。因此本题选C。<br&nbsp;/>', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e9b9a6x3zbg208w040ws8.gif', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1428', '9', '驾驶机动车在这种情况下怎样做？', '遇到这种情况时，不能超车，因此本题选D。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5izyw3y57j20dw05kaak.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1429', '9', '驾驶机动车遇到这种情况怎样行驶？', '遇到这种情况时要减速，并让非机动车先通过，因此本题选A。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j01kwhl2j20dw05kgm4.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1430', '9', '在这种情况下被超机动车驾驶人怎样应对？', '虽然遇到机动车这样超车，会心里不爽，但为了安全起见，要让对方先通过，因此本题选B。<br&nbsp;/>', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j048ld30j20dw05kjrv.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1431', '9', '超车时前方机动车不减速、不让道，驾驶人怎么办？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1432', '9', '超车时，发现前方机动车正在超车，驾驶人怎么办？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1433', '9', '驾驶机动车在这个路段允许超车。', null, '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j06v9mksj20dw05kgm5.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1434', '9', '驾驶机动车在这种道路上从前车右侧超越最安全。', '超车要从左侧超车，根据如图的情况来看，还是放弃超车比较好。因此本题错误。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j09hotmaj20dw05kdgc.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1435', '9', '驾驶机动车在这样的道路上只能从左侧超越。', '超车要从左侧超越，因此本题正确。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j0c3y2l2j20dw05kq3e.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1436', '9', '驾驶机动车在这种情况下可以越过中心实线超车。', '中心实线不能越线超车，而且在前方有障碍物的情况，应当避免超车。因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j0eqbvhkj20dw05kgm2.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1437', '9', '驾驶机动车遇到这种情况要主动减速让后车超越。', '由图可知，后车有超车意图，根据本车的情况，应当予以让行，因此本题正确。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j0hchjsmj20dw05kmxq.jpg', '1', '2017-05-09 21:14:06', '2017-05-09 21:14:06');
INSERT INTO `question` VALUES ('1438', '9', '机动车在这种情况下可以超车。', '根据《中华人民共和国道路交通安全法实施条例》的规定，行经繁华路段、交叉路口、铁路道口、人行横道、急弯路、宽度不足4米的窄路或者窄桥、陡坡、隧道或者容易发生危险的路段，不得超车。<br&nbsp;/><br&nbsp;/>因此本题错误。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j0jz6377j20dw05kweq.jpg', '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1439', '9', '驾驶机动车在这种道路上超车可借对向车道行驶。', '黄色单实线是不能越线超车的，因此本题错误。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j0mlgemjj20dw05kq3g.jpg', '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1440', '9', '驾驶机动车在这种情况下不能超车。', '有图可猜测前方是铁路道口，而根据《中华人民共和国道路交通安全法实施条例》规定，在铁路道口是不能超车的，因此本题正确。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j0p7srjvj20dw05kaak.jpg', '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1441', '9', '如动画所示，在道路上超车时，应尽量加大横向距离，必要时可越实线超车。', '实线是不能碾压的。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e9b9cbri75g208w040gxu.gif', '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1442', '9', '预计在超车过程中与对面来车有会车可能时，应提前加速超越。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1443', '9', '机动车通过急转弯路段时，在机动车较少的情况下可以超车。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1444', '9', '道路划设专用车道的，在专用车道内，其他机动车可以借道超车。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1445', '9', '机动车行经交叉路口，不得超车。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1446', '9', '驾乘人员下车时要怎样做以保证安全？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1447', '9', '机动车在雨天临时停车时，应开启什么灯？', '下雨天开车视野不清，因此若要临时停车，应当开启危险报警闪光灯，提醒其他车辆注意。另外三个选项，没有警示作用一说。', '2', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1448', '9', '机动车在雾天临时停车时，应开启什么灯？', '雾天开车视野不良，因此若要停车，就应该开启危险报警闪光灯，提醒其他车辆注意。', '2', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1449', '9', '机动车在夜间临时停车时，应开启什么灯？', '夜间行驶时视野不良，因此若要停车，应当开启危险报警闪光灯，提醒其他车辆注意。', '2', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1450', '9', '机动车在雪天临时停车时，应开启什么灯？', '报警灯就是前后两黄灯的一亮一灭，四个灯是同时的。<br&nbsp;/><br&nbsp;/>示廓灯就是指表示整车轮廓的，在车的最边最高处，一般是红色小灯。<br&nbsp;/><br&nbsp;/>后位灯一般是指让后面车辆看到本车而使用的，一般是红色，晚上打开车灯时发亮。<br&nbsp;/><br&nbsp;/>因此本题选D。', '2', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1451', '9', '机动车停车的错误做法是什么？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:07', '2017-05-09 21:14:07');
INSERT INTO `question` VALUES ('1452', '9', '在这个区域内可以临时停车。', '如图是禁止临时或长时停车标志，因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j0uhesrpj20dw05k74t.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1453', '9', '在这个区域内不允许长时间停放机动车。', '如图是禁止长时间停车标志，因此本题正确。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j0x39sw8j20dw05k3z2.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1454', '9', '驾驶机动车在前方路口怎样掉头？', '在左转待转区是要左转的车辆，与掉头车辆无关。选项C错在方可二字，因此本题选B。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j0zq977ej20dw05kmxl.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1455', '9', '驾驶机动车在前方路口掉头前先进入左转直行车道。', '由图可知有掉头车道，因此若要掉头应当提前驶入掉头车道内，所以本题错误。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j12dao05j20dw05k0t7.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1456', '9', '驾驶机动车在这个路口允许掉头。', '由图可知此为直行车道，因此在这个路口不能掉头。所以本题错误。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j14zpw77j20dw05k74t.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1457', '9', '驾驶机动车进入左侧车道可以掉头。', '由图可知，道路中心线为黄色虚实线，虚线一侧可以掉头，因此本题正确。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j17mjw0cj20dw05kjrv.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1458', '9', '驾驶机动车需要掉头时，只要不影响正常交通可以在虚线处掉头。', '黄色虚线中心线处是可以掉头的，因此本题正确。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j1a8y0wuj20dw05kgm3.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1459', '9', '驾驶机动车在该处不影响行人正常通行的情况下可以掉头。', '由图可知是黄色双实线，且本车道为直行车道，因此不能掉头，所以本题错误。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j1cw0floj20dw05j74r.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1460', '9', '驾驶机动车在这个路口可以沿掉头车道直接掉头。', '《中华人民共和国道路交通安全法实施条例》第四十九条&nbsp;&nbsp;机动车在有禁止掉头或者禁止左转弯标志、标线的地点以及在铁路道口、人行横道、桥梁、急弯、陡坡、隧道或者容易发生危险的路段，不得掉头。<br&nbsp;/><br&nbsp;/>而本题的情况是在坡上，因此本题错误。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j1fil5ylj20dw05kmxp.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1461', '9', '机动车倒车时遇到这种情况怎样做以保证安全？', '车辆倒车时不得影响其他车辆的正常行驶，因此倒车时发现有状况时，要注意避让，必要时停车等待倒车时机。因此本题选B。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j1i54u3vj20dw05kmxm.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1462', '9', '发生该事故的主要原因是驾驶人倒车前没有进行安全确认。', null, '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j1ks6shxj20dw05kjri.jpg', '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1463', '9', '机动车倒车时，后方道路条件较好的，应加速倒车，迅速完成操作。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1464', '9', '机动车可以选择交叉路口进行倒车。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:08', '2017-05-09 21:14:08');
INSERT INTO `question` VALUES ('1465', '9', '机动车不得在隧道中倒车。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1466', '9', '夜间驾驶机动车在窄路或者窄桥遇自行车对向驶来时，要怎样使用灯光？', '根据《中华人民共和国道路交通安全法实施条例》第四十八条&nbsp;在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：', '2', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1467', '9', '夜间机动车通过照明条件良好的路段时，要怎样使用灯光？', null, '2', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1468', '9', '机动车在夜间通过没有交通信号灯控制的交叉路口时，要怎样使用灯光？', '《中华人民共和国道路交通安全法实施条例》第五十九条&nbsp;&nbsp;机动车在夜间通过急弯、坡路、拱桥、人行横道或者没有交通信号灯控制的路口时，应当交替使用远近光灯示意。', '2', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1469', '9', '夜间驾驶机动车遇到这种情况怎样使用灯光？', '&nbsp;&nbsp;第四十八条&nbsp;在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离；<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（二）在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行；<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（三）在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（四）在狭窄的山路，不靠山体的一方先行；<br&nbsp;/>&nbsp;&nbsp;<strong>&nbsp;&nbsp;（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。<br&nbsp;/>&nbsp;&nbsp;</strong>', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j1nf3gyuj20dw05kaa4.jpg', '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1470', '9', '夜间驾驶机动车在照明条件良好的路段跟车行驶怎样使用灯光？', '&nbsp;&nbsp;第四十八条&nbsp;在没有中心隔离设施或者没有中心线的道路上，机动车遇相对方向来车时应当遵守下列规定：<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（一）减速靠右行驶，并与其他车辆、行人保持必要的安全距离；<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（二）在有障碍的路段，无障碍的一方先行；但有障碍的一方已驶入障碍路段而无障碍的一方未驶入时，有障碍的一方先行；<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（三）在狭窄的坡路，上坡的一方先行；但下坡的一方已行至中途而上坡的一方未上坡时，下坡的一方先行；<br&nbsp;/>&nbsp;&nbsp;&nbsp;&nbsp;（四）在狭窄的山路，不靠山体的一方先行；<br&nbsp;/>&nbsp;&nbsp;<strong>&nbsp;&nbsp;（五）夜间会车应当在距相对方向来车150米以外改用近光灯，在窄路、窄桥与非机动车会车时应当使用近光灯。<br&nbsp;/>&nbsp;&nbsp;</strong>', '2', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1471', '9', '机动车驶入双向行驶隧道前，要如何使用灯光？', '凡是车辆进入双向行驶隧道，一般都应该开启近光灯，而不要开启远光灯。<br&nbsp;/><br&nbsp;/>若是隧道本身没有灯光的，可以变换灯光行驶，但是会车前要转为近光灯。', '2', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1472', '9', '夜间驾驶机动车在照明条件良好的路段可以不使用灯光。', '用近光灯不会照得对方看不见路，同时为了安全起见，开启近光灯能够让相互间清晰可见。因此本题错误。', '1', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1473', '9', '在行驶中，驾驶人在注意与前车保持安全距离的同时，也要谨慎制动，防止被后车追尾。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1474', '9', '在道路上跟车行驶时，跟车距离不是主要的，只须保持与前车相等的速度，即可防止发生追尾事故。', null, '1', '4', '3', null, '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1475', '9', '如动画所示，驾驶机动车遇到这种情况要如何处置？', '由动画可知前方是向左急转弯道路，此时应当减速靠右行，避免对向有来车发生碰撞，因此本题选D。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e9b9elc0nog208w040nis.gif', '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1476', '9', '驾驶机动车遇到这种情况怎样做行驶最安全？', '由图可知前方是弯道，且无法获知前方道路情况，因此应当减速靠右侧行驶，以免发生碰撞。所以本题选B。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j1snwnzmj20dw05kjrx.jpg', '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1477', '9', '在这种条件的道路上怎样行驶才安全？', '前方是向右急转弯路段，靠右转弯是小弯，靠左转弯是大弯，因此本题选C。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j1vavk8hj20dw05kq3e.jpg', '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1478', '9', '在这种条件的道路上怎样安全行驶？', '由图可知，前方是向左急转弯道路，首先应当靠右侧行驶，靠左转的是小弯，靠右转的是大弯，因此本题选D。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j1xycl6vj20dw05kq3g.jpg', '1', '2017-05-09 21:14:09', '2017-05-09 21:14:09');
INSERT INTO `question` VALUES ('1479', '9', '驾驶机动车在这种条件的弯道处怎样转弯最安全？', '在对向有来车时出现弯道时，应当减速靠右侧行驶，因此本题选A。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j20l8odbj20dw05k0t4.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1480', '9', '机动车遇有急弯路时要在进入弯路后减速。', '遇到急弯路应当提前减速慢行，等到进入弯路后减速很容易发生侧滑或侧翻。因此本题错误。', '1', '4', '3', null, '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1481', '9', '机动车行驶至转弯路段时，易引发事故的驾驶行为有什么？', null, '3', '4', '3', null, '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1482', '9', '如动画所示，驾驶机动车遇到这种情况要如何处置？', '由动画可知，前方有行人横穿道路，此时应当注意避让，采取减速的方式，必要时停车让行。因此本题选A。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e9b9hm96tbg208w0401ai.gif', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1483', '9', '驾驶机动车直行通过前方路口怎样行驶？', '前方是没有交通信号灯的交叉路口，应当提前减速慢行，确认安全后通过。因此本题选A。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j25ue2utj20dw05kmxm.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1484', '9', '驾驶机动车在这个路口怎样左转弯行驶？', '路口施划了左转弯待转区，因此左转弯时应当先进入左转弯待转区后再左转，而且此时是直行车道的绿灯信号。因此应当在左转弯待转区等待，所以本题选B。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j28gdlabj20dw05k74u.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1485', '9', '驾驶机动车在这个路口怎样右转弯行驶？', '车辆右转弯不受此红灯信号限制，因此只要沿右侧道路右转弯即可。所以本题选C。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j2b2bdwoj20dw05k3yx.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1486', '9', '驾驶机动车在路口直行遇到这种情况怎么办？', '虽然前方绿灯，可以正常行驶通过，但由于对向有来车左转弯，因此应当让其通过后再通过，所以本题选D。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j2dogwj6j20dw05kdge.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1487', '9', '驾驶机动车在路口遇到这种情况的行人怎么办？', '虽然横穿道路的行人算乱穿，但面对这样的情况驾驶员依旧要减速慢行，必要时停车避让，因此本题选A。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j2gb79xzj20dw05kdge.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1488', '9', '驾驶机动车进入这个路口怎样使用灯光？', '由图可知，前方是向右环形路段，因此向右行驶时不需要开启转向灯，所以本题选C。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j2ix2rlcj20dw05kq3c.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1489', '9', '驾驶机动车驶出这个环岛路口怎样使用灯光？', '由图可知，前方有两条道可供行驶，而驾驶车辆若想要离开环形路口，就要靠右侧行驶转出，此时要开启右转向灯告知其他车辆你要离开了。因此本题选D。<br&nbsp;/>', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j2ljxnqhj20dw05k74r.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1490', '9', '驾驶机动车驶近前方主支干道交汇处要注意什么？', '由图可知，前方会有车辆从支路汇入主路，因此为了避免支路的车辆汇入时发生碰撞，所以要减速慢行，并注意周围的机动车，因此本题选A。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j2o6qxfbj20dw05k3z1.jpg', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1491', '9', '如动画所示，驾驶人的行为是正确的。', '动画中的车辆右转的车道不对，如果是右转应该在在最右侧的车道向右转！', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e9b9jtomr5g208w040qg6.gif', '1', '2017-05-09 21:14:10', '2017-05-09 21:14:10');
INSERT INTO `question` VALUES ('1492', '9', '驾驶机动车通过这个路口要注意观察左侧情况。', '从图上看，是有机动车从右边道路驶出，因此要注意观察该车的行驶路线。所以本题错误。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j2thsqy9j20dw05kq3f.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1493', '9', '驾驶机动车在交叉路口遇到这种情况可以不让行。', '虽然绿灯时可以直行，但由于对向左转车辆已经转到如图位置了，因此应当予以让行，否则会发生碰撞，所以本题错误。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j2w3wmnoj20dw05cdgc.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1494', '9', '驾驶机动车在这个路口右转弯可以不变更车道。', '由图可知，此为三车道道路，右转弯时应当靠右侧车道，因此如图情形是不可以直接右转弯的，因为车的右侧是白实线，不能碾压。因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j2yqlvz3j20dw05k74p.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1495', '9', '驾驶机动车在这个路口可以直接向右转弯。', '遇到这样的情况，应当开启右转向灯，确认右侧没有非机动车接近时右转弯，避免发生碰撞，因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j31cqchyj20dw05kq3f.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1496', '9', '在这个路口左转弯要靠路口中心点左侧转弯。', '如图是中心圈，设在平面交叉路口的中心，用以区分车辆大、小转弯，及交叉路口车辆左右转弯的指示，车辆不得压线行驶。因此本题正确。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j33z6gctj20dw05kwey.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1497', '9', '驾驶机动车在这个路口左转弯要提前按导向箭头指示向左变更车道。', '由图可知，左侧车道是左转弯车道，车辆若要左转弯应当提前变更车道驶入左转弯车道，因此本题正确。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j36md96fj20dw05jwey.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1498', '9', '驾驶机动车此时可以加速通过路口。', '由图可知，此时信号灯已经变为黄色，即将转变为红色，此时应当在停车线前停车。否则就是闯黄灯的行为了。因此本题错误。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j39805cfj20dw05kq3e.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1499', '9', '驾驶机动车在这个路口右转弯时要避让非机动车。', '右转弯时在非机动车道有非机动车正在前进，此时应当注意避让，待非机动车通过后再转弯，因此本题正确。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j3btvnmrj20dw05k74s.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1500', '9', '驾驶机动车怎样通过这个铁路道口？', '由图可知，前方是既没有交通信号灯也没有管理人员看守的铁路道口，在这样的情况下若想要通过，就要一停二看三通过，以确保安全。因此本题选B。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j3egme1lj20dw05kaaf.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1501', '9', '驾驶机动车怎样安全通过铁路道口？', '由图可知，前方是有管理人员看守且有交通信号灯的铁路道口，为避免有状况发生，要在进入道口前减速减档，以免在通过时出现熄火或停车的情况，因此本题选C。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j3h2dbu8j20dw05kq3c.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1502', '9', '如动画所示，机动车通过铁路道口的做法是正确的。', '由动画可知，前方是无人看守的铁路道口，并设有停车让行标志，而驾驶人做到了停车观看，因此本题正确。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e9b9m1ybawg208w0404ft.gif', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1503', '9', '驾驶机动车不能快速通过这种情况的铁路道口。（2.16.3）', null, '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j3mdfamcj20dw05kmxi.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1504', '9', '驾驶机动车通过这个铁路道口时要减速停车。', '红灯亮了呀亲。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j3ozn0gxj20dw05kmxk.jpg', '1', '2017-05-09 21:14:11', '2017-05-09 21:14:11');
INSERT INTO `question` VALUES ('1505', '9', '如动画所示，驾驶机动车遇到这种情况要如何处置？', '由动画可知，前方是人行横道，遇到这种情况应当一看二慢三通过，因此本题选D。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e9b9oayhr2g208w040asw.gif', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1506', '9', '驾驶机动车在这个位置怎样安全通过？', '肯定是停车等待，顺便看MM！HOHO！', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j3uamij0j20dw05k3z1.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1507', '9', '驾驶机动车遇到这种情况的人行横道怎样通过？', '由图可知，这是一个既没有交通信号灯也没有交警指挥的交叉路口，因此通过时一定要减速慢行，并注意观察周围情况，因此本题选A。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j3wx1krhj20dw05kaah.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1508', '9', '造成这起事故的主要原因是行人从车前横穿。', '由图可知，行人正从人行横道线上穿过，发生这样的事故主要是由于车子没有停车让行造成的，因此本题错误。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j3zk4m7nj20dw05kglx.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1509', '9', '驾驶机动车遇到这种情况的人行横道线可以加速通过。', '遇到前方有人行横道应减速慢行，接近时停车让行。而由图中更可知有行人即将穿过，因此本题错误。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j426g09tj20dw05kaam.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1510', '9', '驾驶机动车在人行横道前遇到这种情况一定要减速慢行。', '由图可知，有行人正从人行横道线上横穿道路，而机动车已临近人行横道线，此时应停车让行，以免相撞，因此本题错误。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j44tqv91j20dw05kgm3.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1511', '9', '驾驶机动车看到路边有这种标志时怎样行驶？', '由图可知，前方道路是学校，要注意儿童，因此通过这样的道路时，要减速慢行，同时注意周围情况，必要时停车让行。因此本题选B。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j47h4tzpj20dw05kdgc.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1512', '9', '驾驶机动车在学校门口遇到这种情况怎样行驶？', '由图可知，前方人行横道线上有学生排着队通过，此时应当等学生们通过后再通过。因此在临近人行横道线时，应当停车让行。所以本题选C。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j4a3lyxvj20dw05kgm3.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1513', '9', '驾驶机动车在居民小区看到这种情况怎样安全行驶？', '由图可知，前方由行人想要横穿，因此为了安全起见，要减速慢行，必要时停车避让，因此本题选D。', '2', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j4cpxg8uj20dw05k0ta.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1514', '9', '驾驶机动车通过居民小区遇到这种情况怎样处置？', '看到前方有皮球，就要联想到可能会有儿童跑出来捡球，而且距离较近，因此要停车避让。所以本题选A。', '2', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j4fclkwgj20dw05kjrw.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1515', '9', '驾驶机动车看到这个标志时要及时减速。', '如图前方路段要注意儿童，因此为了避免有儿童突然穿出，要减速慢行，必要时停车避让，所以本题正确。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j4hzl1tzj20dw05kaao.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1516', '9', '在学校门口遇到这种情况要做好随时停车的准备。', '如图是前方路段是学校要注意儿童。因此此时要减速慢行，必要时停车。因此本题正确。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j4km9aynj20dw05kgm5.jpg', '1', '2017-05-09 21:14:12', '2017-05-09 21:14:12');
INSERT INTO `question` VALUES ('1517', '9', '驾驶机动车在学校附近遇到这种情况要尽快加速通过。', '遇到这种情况，要预防有学生在道路上跑动等行为，且前方有人行横道线，因此要减速慢行，必要时停车，所以本题错误。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j4na60dhj20dw05kaak.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1518', '9', '驾驶机动车在居民小区遇到这种情形要连续鸣喇叭。', '图中标志是禁止鸣喇叭标志，也就是在此情形下不得鸣喇叭。因此本题错误。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j4pw8fcvj20dw05kwf0.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1519', '9', '驾驶机动车在居民小区遇到这种情形要紧跟其后行驶。', '有图可知前方是陪着骑车儿童的长辈，以防儿童突然改变方向或是从小车子上掉落，因此要保持一定的安全距离，减速慢行，必要时停车避让。因此本题错误。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j4sjqeiej20dw05kt99.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1520', '9', '驾驶机动车进入居民小区不能超过限速标志限定的速度行驶。', '由图可知，进入此小区车速限制在5公里/小时，也就是说不能超过5公里/小时，因此本题正确。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j4v6u6hfj20dw05kt95.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1521', '9', '驾驶机动车在小区内遇到这样的情况要在自行车前加速通过。', '由图可知，前方是丁字路口，且横向道路有骑自行车的人穿出。遇到这种情况应当让自行车先行，减速慢行注意避让。因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j4xt4vqlj20dw05k3z2.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1522', '9', '驾驶机动车通过学校时要注意什么？', '在有学校的路段一般都会设置提醒有学校或是禁止鸣喇叭的交通标志，因此在经过学校时不仅要观察交通标志，还要记住不得鸣喇叭。另外，由于学校周围会有学生走动，因此在途经时车速一定要慢，避免撞到人或其他。因此本题要选ABC。', '3', '4', '3', null, '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1523', '9', '驾驶机动车在这种情况下注意什么？', '由图可知，公交车前方有一名撑伞的女士，因此要考虑到可能这名女士会横穿，所以本题选B。', '2', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5j50gn4myj20dw05k74q.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1524', '9', '在这种公交车站怎样预防公交车突然起步？', '注意本题的情景：公交车可能突然起步。因此横向间距要加大，而且要减速慢行，超越时要特别留意公交车的动态，避免发生碰撞，因此本题选C。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j5348iofj20dw05kjrv.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1525', '9', '驾驶机动车遇到这种情况怎样行驶？', '遇到这种情况，要考虑到公交车可能会突然起步，那么骑自行车的人就会向外侧偏转方向继续行驶，因此驾驶员要减速慢行，必要时停车避让，不能贸然有动作，因此本题选D。', '2', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5j55qoi7aj20dw05kmxo.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1526', '9', '驾驶机动车在这种情况下可以占用公交车站临时停车。', '根据《中华人民共和国道路交通安全法实施条例》第六十三条&nbsp;&nbsp;机动车在道路上临时停车，应当遵守下列规定：<br&nbsp;/><br&nbsp;/>（三）公共汽车站、急救站、加油站、消防栓或者消防队（站）门前以及距离上述地点30米以内的路段，除使用上述设施的以外，不得停车；<br&nbsp;/><br&nbsp;/>因此本题错误。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5j58cwayuj20dw05kq3i.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
INSERT INTO `question` VALUES ('1527', '9', '驾驶机动车在公交车站遇到这种情况要迅速停车让行。', '由图可知，停靠在一边的公交车后有行人穿出，此时应当予以避让，其一是为了避让行人，其二是以防公交车突然起步。因此本题正确。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5j5aznd5lj20dw05k0t9.jpg', '1', '2017-05-09 21:14:13', '2017-05-09 21:14:13');
