INSERT INTO `question` VALUES ('1725', '11', '右侧标志警告前方150米是无人看守铁路道口。', '火车头是无人看守&nbsp;铁道是有人看守&nbsp;&nbsp;横着一杠50&nbsp;两杠100&nbsp;三杠150&nbsp;<br&nbsp;/>', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5kjjfb00uj20dw05kaan.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1726', '11', '右侧标志提醒前方是非机动车道。', '黄底的是注意的意思，此标志的意思是注意非机动车。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5kjm1ly80j20dw05kt97.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1727', '11', '右侧标志告知前方注意残疾人。', '黄色三角形的都是--注意**的意思。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5kjoof84vj20dw05k74s.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1728', '11', '右侧标志提醒前方路段保持车距。', '事故易发路段-此标志设在交通事故易发路段以前适当位置。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5kjrb4ieoj20dw05hmxk.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1729', '11', '图中标志提醒障碍物在路中，车辆从两侧绕行。', '左右绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，绕行通过。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5kjtyappzj20dw05k74s.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1730', '11', '图中标志提醒障碍物在路中，车辆从右侧绕行。', '左侧绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，左侧绕行通过。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5kjwkqgx1j20dw05k3yy.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1731', '11', '图中标志提醒障碍物在路中，车辆从左侧绕行。', '右侧绕行-用以告示前方道路有障碍物，车辆应按标志指示减速慢行，右侧绕行通过。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5kjz7l2s9j20dw05kaaj.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1732', '11', '右侧标志提醒前方路段有塌方禁止通行。', '注意危险-用以促使车辆驾驶员谨慎慢行。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5l2qr93baj20dw05ogly.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1733', '11', '右侧标志提醒前方道路正在施工。', '施工标志-用以告示前方道路施工，车辆应减速慢行或绕道行驶。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5l2td4gxlj20dw05kdgd.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1734', '11', '右侧标志提醒前方有村庄或集镇，建议速度30公里/小时。', '上边是村庄标志-此标志设在不易发现前方有村庄或小城镇的路段以前适当位置。<br&nbsp;/>下边是建议车速标志。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5l2vzqowtj20dw05kaam.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1735', '11', '右侧这个标志警告进入隧道减速慢行。', null, '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5l2ym02jqj20dw05kt91.jpg', '1', '2017-05-09 21:14:58', '2017-05-09 21:14:58');
INSERT INTO `question` VALUES ('1736', '11', '左侧标志警告前方注意左侧路口有汇入车辆。', '请记住。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5l318kus3j20dw05kwev.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1737', '11', '右侧标志警告前方注意右侧路口有汇入车辆。', '注意合流&nbsp;<br&nbsp;/>用以警告车辆驾驶人注意前方有车辆汇合进来。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5l33v3joyj20dw05kwf3.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1738', '11', '右侧标志提醒前方右侧500米有避险车道。', '避险车道是指在长陡下坡路段行车道外侧增设的供速度失控车辆驶离正线安全减速的专用车道。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5l36h29t7j20dw05qweu.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1739', '11', '右侧标志表示前方路口要停车让行。', '停车让行-表示车辆必须在停止线以外停车了望，确认安全后，才准许通行。停车让行标志在下列情况下设置：(1)与交通流量较大的干路平交的支路路口；(2)无人看守的铁路道口；(3)其他需要设置的地方。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5l3938688j20dw05kdg9.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1740', '11', '右侧标志表示前方路段会车时停车让对方车先行。', '减速让行-表示车辆应减速让行，告示车辆驾驶员必须慢行或停车，观察干路行车情况，在确保干道车辆优先的前提下，认为安全时方可续行。此标志设在视线良好交叉道路的次要路口。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5l3bq0nlhj20dw05ljrx.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1741', '11', '左侧标志表示前方路段允许进入。', '禁止通行-表示禁止一切车辆和行人通行。此标志设在禁止通行的道路入口处。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5l3eedezqj20dw05kglz.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1742', '11', '右侧标志提示一切车辆都不能驶入。', '禁止驶入-表示禁止车辆驶入。此标志设在禁止驶入的路段入口处。', '1', '4', '3', 'http://ww2.sinaimg.cn/large/ca44ca2ajw1e5l3h1oyj0j20dw05kjrv.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1743', '11', '右侧标志表示前方路口不准车辆左转。', '禁止向左转弯-表示前方路口禁止一切车辆向左转弯。此标志设在禁止向左转弯的路口前适当位置', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5l3jpfhnvj20dw05j3yx.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1744', '11', '右侧标志表示前方路口不准车辆右转。', '禁止向右转弯-表示前方路口禁止一切车辆向右转弯。此标志设在禁止向右转弯的路口前适当位置。', '1', '4', '3', 'http://ww4.sinaimg.cn/large/ca44ca2ajw1e5l3mc9nzpj20dw05k0t3.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1745', '11', '左侧标志表示前方路口不准掉头。', '禁止掉头-表示前方路口禁止一切车辆掉头。此标志设在禁止掉头的路口前适当位置。', '1', '4', '3', 'http://ww3.sinaimg.cn/large/ca44ca2ajw1e5l3oy9cznj20dw05imxn.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
INSERT INTO `question` VALUES ('1746', '11', '右侧标志表示前方路段允许超车。', '禁止超车-表示该标志至前方解除禁止超车标志的路段内，不准机动车超车。此标志设在禁止超车的起点。', '1', '4', '3', 'http://ww1.sinaimg.cn/large/ca44ca2ajw1e5l3rlqycej20dw05kwez.jpg', '1', '2017-05-09 21:14:59', '2017-05-09 21:14:59');
